import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Search, Edit, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import DataTable from "@/components/tables/data-table";
import ProfileModal from "@/components/modals/profile-modal";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { type Profile, type InsertProfile } from "@shared/schema";

export default function Profiles() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedProfile, setSelectedProfile] = useState<Profile | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [typeFilter, setTypeFilter] = useState("all");
  const [statusFilter, setStatusFilter] = useState("all");
  const { toast } = useToast();

  // Fetch profiles from API
  const { data: profiles = [], isLoading } = useQuery<Profile[]>({
    queryKey: ["/api/profiles"],
  });

  // Create profile mutation
  const createProfileMutation = useMutation({
    mutationFn: async (data: InsertProfile) => {
      const response = await apiRequest("POST", "/api/profiles", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/profiles"] });
      toast({
        title: "Success",
        description: "Profile created successfully",
      });
      setIsModalOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create profile",
        variant: "destructive",
      });
    },
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: Partial<InsertProfile> }) => {
      const response = await apiRequest("PUT", `/api/profiles/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/profiles"] });
      toast({
        title: "Success",
        description: "Profile updated successfully",
      });
      setIsModalOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  // Delete profile mutation
  const deleteProfileMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/profiles/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/profiles"] });
      toast({
        title: "Success",
        description: "Profile deleted successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete profile",
        variant: "destructive",
      });
    },
  });

  const filteredProfiles = profiles.filter(profile => {
    const matchesSearch = profile.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         profile.phone.includes(searchTerm);
    const matchesType = typeFilter === "all" || profile.type === typeFilter;
    const matchesStatus = statusFilter === "all" || 
                         (statusFilter === "active" && profile.isActive) ||
                         (statusFilter === "inactive" && !profile.isActive);

    return matchesSearch && matchesType && matchesStatus;
  });

  const handleAddProfile = () => {
    setSelectedProfile(null);
    setIsModalOpen(true);
  };

  const handleEditProfile = (profile: Profile) => {
    setSelectedProfile(profile);
    setIsModalOpen(true);
  };

  const handleSubmitProfile = (data: InsertProfile) => {
    if (selectedProfile) {
      updateProfileMutation.mutate({ id: selectedProfile.id, data });
    } else {
      createProfileMutation.mutate(data);
    }
  };

  const handleDeleteProfile = (profileId: string) => {
    if (confirm("Are you sure you want to delete this profile?")) {
      deleteProfileMutation.mutate(profileId);
    }
  };

  const columns = [
    {
      key: "name",
      title: "Name",
      render: (value: string, row: Profile) => (
        <div>
          <div className="font-medium" data-testid={`profile-name-${row.id}`}>
            {value}
          </div>
          <div className="text-sm text-muted-foreground">
            {row.notes || `${row.type} profile`}
          </div>
        </div>
      ),
    },
    {
      key: "type",
      title: "Type",
      render: (value: string) => (
        <Badge 
          variant={value === "uplink" ? "default" : "secondary"}
          className={
            value === "uplink" 
              ? "bg-primary/10 text-primary" 
              : "bg-green-100 text-green-600"
          }
          data-testid={`profile-type-${value}`}
        >
          {value.charAt(0).toUpperCase() + value.slice(1)}
        </Badge>
      ),
    },
    {
      key: "phone",
      title: "Contact",
      render: (value: string, row: Profile) => (
        <div>
          <div data-testid={`profile-phone-${row.id}`}>{value}</div>
          {row.email && (
            <div className="text-sm text-muted-foreground" data-testid={`profile-email-${row.id}`}>
              {row.email}
            </div>
          )}
        </div>
      ),
    },
    {
      key: "ratePerPoint",
      title: "Rate/Point",
      align: "right" as const,
      render: (value: string) => `â‚¹${parseFloat(value).toFixed(2)}`,
    },
    {
      key: "commissionPercentage",
      title: "Commission",
      align: "right" as const,
      render: (value: string | null) => value ? `${parseFloat(value)}%` : "-",
    },
    {
      key: "actions",
      title: "Actions",
      align: "center" as const,
      render: (_: any, row: Profile) => (
        <div className="flex justify-center space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleEditProfile(row)}
            data-testid={`edit-profile-${row.id}`}
          >
            <Edit className="w-4 h-4 text-primary" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleDeleteProfile(row.id)}
            data-testid={`delete-profile-${row.id}`}
          >
            <Trash2 className="w-4 h-4 text-destructive" />
          </Button>
        </div>
      ),
    },
  ];

  return (
    <div className="p-4 md:p-6" data-testid="profiles-page">
      <div className="flex flex-col md:flex-row md:items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-2" data-testid="profiles-title">
            Uplinks & Downlines
          </h2>
          <p className="text-muted-foreground" data-testid="profiles-description">
            Manage your inventory partners
          </p>
        </div>
        <Button
          onClick={handleAddProfile}
          className="mt-4 md:mt-0"
          data-testid="add-profile-button"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Profile
        </Button>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="search">Search</Label>
              <div className="relative mt-2">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground w-4 h-4" />
                <Input
                  id="search"
                  placeholder="Search profiles..."
                  className="pl-9"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  data-testid="profiles-search-input"
                />
              </div>
            </div>
            <div>
              <Label htmlFor="type-filter">Type</Label>
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="mt-2" data-testid="profiles-type-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="uplink">Uplink</SelectItem>
                  <SelectItem value="downline">Downline</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="status-filter">Status</Label>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="mt-2" data-testid="profiles-status-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="inactive">Inactive</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-end">
              <Button
                variant="secondary"
                className="w-full mt-2"
                onClick={() => {
                  setSearchTerm("");
                  setTypeFilter("all");
                  setStatusFilter("all");
                }}
                data-testid="profiles-clear-filters-button"
              >
                <Search className="w-4 h-4 mr-2" />
                Clear Filters
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Profiles Table */}
      <DataTable
        data={filteredProfiles}
        columns={columns}
        testId="profiles-table"
        loading={isLoading}
      />

      {/* Profile Modal */}
      <ProfileModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleSubmitProfile}
        profile={selectedProfile}
      />
    </div>
  );
}

client/src/pages/reports.tsx.content:
import { useState } from "react";
import { BarChart3, Download, FileText } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import DataTable from "@/components/tables/data-table";
import { useQuery } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";

export default function Reports() {
  const [reportType, setReportType] = useState("profit-loss");
  const [startDate, setStartDate] = useState(() => {
    const date = new Date();
    return new Date(date.getFullYear(), date.getMonth(), 1).toISOString().split('T')[0];
  });
  const [endDate, setEndDate] = useState(() => new Date().toISOString().split('T')[0]);
  const [shouldGenerate, setShouldGenerate] = useState(true);
  const { toast } = useToast();

  // Fetch report data
  const { data: reportResult, isLoading, refetch } = useQuery({
    queryKey: ["/api/reports", { startDate, endDate, reportType }],
    queryFn: () => {
      const params = new URLSearchParams({ 
        startDate, 
        endDate, 
        reportType 
      });
      return fetch(`/api/reports?${params}`).then(res => res.json());
    },
    enabled: shouldGenerate,
  });

  const handleGenerateReport = () => {
    setShouldGenerate(true);
    refetch();
    toast({
      title: "Generating Report",
      description: "Report is being generated with current filters",
    });
  };

  const handleExportPDF = () => {
    toast({
      title: "Feature Coming Soon",
      description: "PDF export functionality will be implemented",
    });
  };

  const handleExportCSV = () => {
    if (!reportResult?.data) {
      toast({
        title: "No Data",
        description: "Please generate a report first",
        variant: "destructive",
      });
      return;
    }

    // Simple CSV export
    const headers = ["Profile", "Type", "Volume", "Revenue", "Commission", "Net P&L"];
    const csvContent = [
      headers.join(","),
      ...reportResult.data.map((row: any) => [
        row.profileName,
        row.type,
        row.volume,
        row.revenue,
        row.commission,
        row.netPL
      ].join(","))
    ].join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = `report-${reportType}-${startDate}-${endDate}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);

    toast({
      title: "CSV Downloaded",
      description: "Report has been exported to CSV",
    });
  };

  const reportData = reportResult?.data || [];
  const summary = reportResult?.summary || {
    totalRevenue: 0,
    totalCosts: 0,
    grossProfit: 0,
    profitMargin: 0
  };

  const columns = [
    {
      key: "profileName",
      title: "Profile",
      render: (value: string) => (
        <div className="font-medium" data-testid={`report-profile-${value}`}>
          {value}
        </div>
      ),
    },
    {
      key: "type",
      title: "Type",
    },
    {
      key: "volume",
      title: "Volume",
      align: "right" as const,
    },
    {
      key: "revenue",
      title: "Revenue",
      align: "right" as const,
    },
    {
      key: "commission",
      title: "Commission",
      align: "right" as const,
    },
    {
      key: "netPL",
      title: "Net P&L",
      align: "right" as const,
      render: (value: string, row: any) => (
        <span 
          className={`font-medium ${row.isProfit ? "text-green-600" : "text-destructive"}`}
          data-testid={`report-pnl-${row.id}`}
        >
          {value}
        </span>
      ),
    },
  ];

  return (
    <div className="p-4 md:p-6" data-testid="reports-page">
      <div className="mb-6">
        <h2 className="text-2xl font-bold mb-2" data-testid="reports-title">
          Custom Reports
        </h2>
        <p className="text-muted-foreground" data-testid="reports-description">
          Generate detailed reports for any date range
        </p>
      </div>

      {/* Report Filters */}
      <Card className="mb-6">
        <CardHeader>
          <CardTitle className="text-lg">Generate Report</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <Label htmlFor="report-type">Report Type</Label>
              <Select value={reportType} onValueChange={setReportType}>
                <SelectTrigger className="mt-2" data-testid="report-type-select">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="profit-loss">Profit/Loss Report</SelectItem>
                  <SelectItem value="balance-summary">Balance Summary</SelectItem>
                  <SelectItem value="transaction-report">Transaction Report</SelectItem>
                  <SelectItem value="commission-analysis">Commission Analysis</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="start-date">Start Date</Label>
              <Input
                id="start-date"
                type="date"
                className="mt-2"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                data-testid="report-start-date-input"
              />
            </div>
            <div>
              <Label htmlFor="end-date">End Date</Label>
              <Input
                id="end-date"
                type="date"
                className="mt-2"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                data-testid="report-end-date-input"
              />
            </div>
            <div className="flex items-end">
              <Button
                onClick={handleGenerateReport}
                className="w-full mt-2"
                data-testid="generate-report-button"
              >
                <BarChart3 className="w-4 h-4 mr-2" />
                Generate
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Sample Report */}
      <Card data-testid="report-results-card">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Profit/Loss Report</CardTitle>
              <p className="text-sm text-muted-foreground" data-testid="report-period">
                {reportResult?.period ? 
                  `${new Date(reportResult.period.start).toLocaleDateString("en-IN")} - ${new Date(reportResult.period.end).toLocaleDateString("en-IN")}` :
                  "Select date range and generate report"
                }
              </p>
            </div>
            <div className="space-x-2">
              <Button
                variant="outline"
                size="sm"
                onClick={handleExportPDF}
                data-testid="export-pdf-button"
              >
                <Download className="w-4 h-4 mr-1" />
                PDF
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={handleExportCSV}
                data-testid="export-csv-button"
              >
                <FileText className="w-4 h-4 mr-1" />
                CSV
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent>
          {/* Report Summary */}
          <div className="p-6 border-b border-border">
            {isLoading ? (
              <div className="text-center text-muted-foreground">Loading report data...</div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-primary" data-testid="total-revenue">
                    â‚¹{summary.totalRevenue.toLocaleString("en-IN")}
                  </div>
                  <div className="text-sm text-muted-foreground">Total Revenue</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-destructive" data-testid="total-costs">
                    â‚¹{summary.totalCosts.toLocaleString("en-IN")}
                  </div>
                  <div className="text-sm text-muted-foreground">Total Costs</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600" data-testid="gross-profit">
                    â‚¹{summary.grossProfit.toLocaleString("en-IN")}
                  </div>
                  <div className="text-sm text-muted-foreground">Gross Profit</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600" data-testid="profit-margin">
                    {summary.profitMargin.toFixed(1)}%
                  </div>
                  <div className="text-sm text-muted-foreground">Profit Margin</div>
                </div>
              </div>
            )}
          </div>

          {/* Detailed Breakdown */}
          <div className="p-0">
            {isLoading ? (
              <div className="p-4 text-center text-muted-foreground">Loading report breakdown...</div>
            ) : reportData.length === 0 ? (
              <div className="p-4 text-center text-muted-foreground">
                No transactions found for the selected period
              </div>
            ) : (
              <DataTable
                data={reportData}
                columns={columns}
                testId="report-breakdown-table"
              />
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/settlement.tsx.content:
import { useState } from "react";
import { Send, Eye, RotateCcw, CheckCircle, Clock } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import DataTable from "@/components/tables/data-table";
import SettlementModal from "@/components/modals/settlement-modal";
import MessagePreviewModal from "@/components/modals/message-preview-modal";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { type Settlement, type Profile } from "@shared/schema";

export default function Settlement() {
  const [isSettlementModalOpen, setIsSettlementModalOpen] = useState(false);
  const [isPreviewModalOpen, setIsPreviewModalOpen] = useState(false);
  const [selectedSettlement, setSelectedSettlement] = useState<Settlement | null>(null);
  const { toast } = useToast();

  // Fetch settlements and profiles
  const { data: settlements = [], isLoading: isLoadingSettlements } = useQuery<Settlement[]>({
    queryKey: ["/api/settlements"],
  });

  const { data: profiles = [] } = useQuery<Profile[]>({
    queryKey: ["/api/profiles"],
  });

  // Create settlement mutation
  const createSettlementMutation = useMutation({
    mutationFn: (data: any) => apiRequest("POST", "/api/settlements", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/settlements"] });
      setIsSettlementModalOpen(false);
      toast({
        title: "Success",
        description: "Settlement sent successfully",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to send settlement",
        variant: "destructive",
      });
    },
  });

  const handleSendSettlement = (data: any) => {
    createSettlementMutation.mutate(data);
  };

  const handlePreviewMessage = (settlement: Settlement) => {
    setSelectedSettlement(settlement);
    setIsPreviewModalOpen(true);
  };

  const handleResendMessage = (settlementId: string) => {
    toast({
      title: "Info",
      description: "Resend functionality will be implemented with WhatsApp integration",
    });
  };

  const selectedProfile = selectedSettlement 
    ? profiles.find(p => p.id === selectedSettlement.profileId) || null
    : null;

  const columns = [
    {
      key: "createdAt",
      title: "Date",
      render: (value: string | Date) => {
        const date = typeof value === "string" ? new Date(value) : value;
        return date.toLocaleDateString("en-IN");
      },
    },
    {
      key: "period",
      title: "Period",
      render: (value: string) => {
        // Handle YYYY-MM format
        if (value.match(/^\d{4}-\d{2}$/)) {
          const [year, month] = value.split('-');
          const date = new Date(parseInt(year), parseInt(month) - 1);
          return date.toLocaleDateString("en-IN", { year: 'numeric', month: 'long' });
        }
        // Fallback for other formats
        return value;
      },
    },
    {
      key: "profileId",
      title: "Profile",
      render: (value: string) => {
        const profile = profiles.find(p => p.id === value);
        return profile?.name || "Unknown";
      },
    },
    {
      key: "amount",
      title: "Amount",
      align: "right" as const,
      render: (value: string) => `â‚¹${parseFloat(value).toLocaleString("en-IN")}`,
    },
    {
      key: "status",
      title: "Status",
      align: "center" as const,
      render: (value: string) => {
        const statusConfig = {
          sent: { icon: CheckCircle, color: "text-green-600", bg: "bg-green-100", label: "Sent" },
          pending: { icon: Clock, color: "text-yellow-600", bg: "bg-yellow-100", label: "Pending" },
          failed: { icon: RotateCcw, color: "text-red-600", bg: "bg-red-100", label: "Failed" },
        };

        const config = statusConfig[value as keyof typeof statusConfig];
        const Icon = config.icon;

        return (
          <Badge 
            variant="outline"
            className={`${config.bg} ${config.color}`}
            data-testid={`settlement-status-${value}`}
          >
            <Icon className="w-3 h-3 mr-1" />
            {config.label}
          </Badge>
        );
      },
    },
    {
      key: "actions",
      title: "Actions",
      align: "center" as const,
      render: (_: any, row: Settlement) => (
        <div className="flex justify-center space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handlePreviewMessage(row)}
            data-testid={`preview-settlement-${row.id}`}
          >
            <Eye className="w-4 h-4 text-primary" />
          </Button>
          {row.status !== "sent" && (
            <Button
              variant="ghost"
              size="icon"
              onClick={() => handleResendMessage(row.id)}
              data-testid={`resend-settlement-${row.id}`}
            >
              <RotateCcw className="w-4 h-4 text-primary" />
            </Button>
          )}
        </div>
      ),
    },
  ];

  return (
    <div className="p-4 md:p-6" data-testid="settlement-page">
      <div className="flex flex-col md:flex-row md:items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-2" data-testid="settlement-title">
            Settlement & WhatsApp
          </h2>
          <p className="text-muted-foreground" data-testid="settlement-description">
            Automated settlement reports via WhatsApp
          </p>
        </div>
        <Button
          onClick={() => setIsSettlementModalOpen(true)}
          className="mt-4 md:mt-0"
          data-testid="send-settlement-button"
        >
          <Send className="w-4 h-4 mr-2" />
          Send Settlement
        </Button>
      </div>

      {/* Settlement Status */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
        <Card data-testid="next-settlement-card">
          <CardHeader>
            <CardTitle className="text-lg">Next Settlement</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="flex justify-between items-center">
                <span className="text-sm text-muted-foreground">Scheduled Date:</span>
                <span className="font-medium" data-testid="next-settlement-date">
                  {(() => {
                    const nextMonday = new Date();
                    nextMonday.setDate(nextMonday.getDate() + (1 + 7 - nextMonday.getDay()) % 7);
                    return nextMonday.toLocaleDateString("en-IN", { 
                      weekday: 'long', 
                      year: 'numeric', 
                      month: 'short', 
                      day: 'numeric' 
                    });
                  })()}
                </span>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-sm text-muted-foreground">Profiles to Settle:</span>
                <span className="font-medium" data-testid="next-settlement-profiles">
                  {profiles.filter(p => p.isActive).length} profiles
                </span>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-sm text-muted-foreground">Auto Send:</span>
                <Badge className="bg-green-100 text-green-600" data-testid="auto-send-status">
                  Enabled
                </Badge>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card data-testid="whatsapp-status-card">
          <CardHeader>
            <CardTitle className="text-lg">WhatsApp Status</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <div className="flex justify-between items-center">
                <span className="text-sm text-muted-foreground">API Status:</span>
                <Badge className="bg-green-100 text-green-600" data-testid="whatsapp-api-status">
                  <CheckCircle className="w-3 h-3 mr-1" />
                  Connected
                </Badge>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-sm text-muted-foreground">Messages Sent Today:</span>
                <span className="font-medium" data-testid="messages-sent-today">
                  {settlements.filter(s => {
                    const today = new Date().toDateString();
                    return s.createdAt && new Date(s.createdAt).toDateString() === today;
                  }).length}
                </span>
              </div>
              <div className="flex justify-between items-center">
                <span className="text-sm text-muted-foreground">Failed Deliveries:</span>
                <span className="font-medium text-destructive" data-testid="failed-deliveries">
                  {settlements.filter(s => s.status === "failed").length}
                </span>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Settlement History */}
      <Card data-testid="settlement-history-card">
        <CardHeader>
          <CardTitle>Settlement History</CardTitle>
        </CardHeader>
        <CardContent className="p-0">
          {isLoadingSettlements ? (
            <div className="p-4 text-center text-muted-foreground">Loading settlements...</div>
          ) : (
            <DataTable
              data={settlements}
              columns={columns}
              testId="settlement-history-table"
            />
          )}
        </CardContent>
      </Card>

      {/* Settlement Modal */}
      <SettlementModal
        isOpen={isSettlementModalOpen}
        onClose={() => setIsSettlementModalOpen(false)}
        onSubmit={handleSendSettlement}
      />

      {/* Message Preview Modal */}
      <MessagePreviewModal
        isOpen={isPreviewModalOpen}
        onClose={() => setIsPreviewModalOpen(false)}
        settlement={selectedSettlement}
        profile={selectedProfile}
      />
    </div>
  );
}

client/src/pages/transactions.tsx.content:
import { useState } from "react";
import { Plus, Filter, Edit, Trash2, TrendingDown, TrendingUp } from "lucide-react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import DataTable from "@/components/tables/data-table";
import TransactionModal from "@/components/modals/transaction-modal";
import { type Transaction, type InsertTransaction, type Profile } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function Transactions() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedTransaction, setSelectedTransaction] = useState<Transaction | null>(null);
  const [dateFrom, setDateFrom] = useState("");
  const [dateTo, setDateTo] = useState("");
  const [typeFilter, setTypeFilter] = useState("all");
  const [profileFilter, setProfileFilter] = useState("all");
  const { toast } = useToast();

  // Fetch transactions from API
  const { data: transactions = [], isLoading: transactionsLoading } = useQuery<Transaction[]>({
    queryKey: ["/api/transactions"],
  });

  // Fetch profiles from API
  const { data: profiles = [], isLoading: profilesLoading } = useQuery<Profile[]>({
    queryKey: ["/api/profiles"],
  });

  const filteredTransactions = transactions.filter(transaction => {
    const transactionDate = new Date(transaction.date).toISOString().split('T')[0];
    const matchesDateFrom = !dateFrom || transactionDate >= dateFrom;
    const matchesDateTo = !dateTo || transactionDate <= dateTo;
    const matchesType = typeFilter === "all" || transaction.type === typeFilter;
    const matchesProfile = profileFilter === "all" || transaction.profileId === profileFilter;

    return matchesDateFrom && matchesDateTo && matchesType && matchesProfile;
  });

  const handleAddTransaction = () => {
    setSelectedTransaction(null);
    setIsModalOpen(true);
  };

  const handleEditTransaction = (transaction: Transaction) => {
    setSelectedTransaction(transaction);
    setIsModalOpen(true);
  };

  // Create transaction mutation
  const createTransactionMutation = useMutation({
    mutationFn: (data: InsertTransaction) => apiRequest("POST", "/api/transactions", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      toast({
        title: "Success",
        description: "Transaction created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create transaction",
        variant: "destructive",
      });
    },
  });

  // Update transaction mutation
  const updateTransactionMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<InsertTransaction> }) => 
      apiRequest("PUT", `/api/transactions/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      toast({
        title: "Success",
        description: "Transaction updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update transaction",
        variant: "destructive",
      });
    },
  });

  // Delete transaction mutation
  const deleteTransactionMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/transactions/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/transactions"] });
      toast({
        title: "Success",
        description: "Transaction deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete transaction",
        variant: "destructive",
      });
    },
  });

  const handleSubmitTransaction = (data: InsertTransaction) => {
    if (selectedTransaction) {
      updateTransactionMutation.mutate({ id: selectedTransaction.id, data });
    } else {
      createTransactionMutation.mutate(data);
    }
  };

  const handleDeleteTransaction = (transactionId: string) => {
    if (confirm("Are you sure you want to delete this transaction?")) {
      deleteTransactionMutation.mutate(transactionId);
    }
  };

  const columns = [
    {
      key: "date",
      title: "Date",
      render: (value: Date | string) => {
        const date = value instanceof Date ? value : new Date(value);
        return date.toLocaleDateString("en-IN");
      },
    },
    {
      key: "type",
      title: "Type",
      render: (value: string) => (
        <Badge 
          variant={value === "taken" ? "default" : "secondary"}
          className={
            value === "taken" 
              ? "bg-primary/10 text-primary" 
              : "bg-green-100 text-green-600"
          }
          data-testid={`transaction-type-${value}`}
        >
          {value === "taken" ? (
            <>
              <TrendingDown className="w-3 h-3 mr-1" />
              Taken
            </>
          ) : (
            <>
              <TrendingUp className="w-3 h-3 mr-1" />
              Given
            </>
          )}
        </Badge>
      ),
    },
    {
      key: "profileId",
      title: "Profile",
      render: (value: string) => {
        const profile = profiles.find(p => p.id === value);
        return profile?.name || "Unknown";
      },
    },
    {
      key: "points",
      title: "Points",
      align: "right" as const,
      render: (value: number) => value.toLocaleString("en-IN"),
    },
    {
      key: "ratePerPoint",
      title: "Rate",
      align: "right" as const,
      render: (value: string) => `â‚¹${parseFloat(value).toFixed(2)}`,
    },
    {
      key: "commissionPercentage",
      title: "Commission",
      align: "right" as const,
      render: (value: string | null) => value ? `${parseFloat(value)}%` : "-",
    },
    {
      key: "totalAmount",
      title: "Total Amount",
      align: "right" as const,
      render: (value: string) => `â‚¹${parseFloat(value).toLocaleString("en-IN")}`,
    },
    {
      key: "actions",
      title: "Actions",
      align: "center" as const,
      render: (_: any, row: Transaction) => (
        <div className="flex justify-center space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleEditTransaction(row)}
            data-testid={`edit-transaction-${row.id}`}
          >
            <Edit className="w-4 h-4 text-primary" />
          </Button>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleDeleteTransaction(row.id)}
            data-testid={`delete-transaction-${row.id}`}
          >
            <Trash2 className="w-4 h-4 text-destructive" />
          </Button>
        </div>
      ),
    },
  ];

  return (
    <div className="p-4 md:p-6" data-testid="transactions-page">
      <div className="flex flex-col md:flex-row md:items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-2" data-testid="transactions-title">
            Inventory Transactions
          </h2>
          <p className="text-muted-foreground" data-testid="transactions-description">
            Track all inventory movements
          </p>
        </div>
        <Button
          onClick={handleAddTransaction}
          className="mt-4 md:mt-0"
          data-testid="add-transaction-button"
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Transaction
        </Button>
      </div>

      {/* Transaction Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
            <div>
              <Label htmlFor="date-from">Date From</Label>
              <Input
                id="date-from"
                type="date"
                className="mt-2"
                value={dateFrom}
                onChange={(e) => setDateFrom(e.target.value)}
                data-testid="transactions-date-from-input"
              />
            </div>
            <div>
              <Label htmlFor="date-to">Date To</Label>
              <Input
                id="date-to"
                type="date"
                className="mt-2"
                value={dateTo}
                onChange={(e) => setDateTo(e.target.value)}
                data-testid="transactions-date-to-input"
              />
            </div>
            <div>
              <Label htmlFor="type-filter">Type</Label>
              <Select value={typeFilter} onValueChange={setTypeFilter}>
                <SelectTrigger className="mt-2" data-testid="transactions-type-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="taken">Inventory Taken</SelectItem>
                  <SelectItem value="given">Inventory Given</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <div>
              <Label htmlFor="profile-filter">Profile</Label>
              <Select value={profileFilter} onValueChange={setProfileFilter}>
                <SelectTrigger className="mt-2" data-testid="transactions-profile-filter">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Profiles</SelectItem>
                  {profiles.map((profile) => (
                    <SelectItem key={profile.id} value={profile.id}>
                      {profile.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="flex items-end">
              <Button
                variant="secondary"
                className="w-full mt-2"
                onClick={() => {
                  setDateFrom("");
                  setDateTo("");
                  setTypeFilter("all");
                  setProfileFilter("all");
                }}
                data-testid="transactions-apply-filters-button"
              >
                <Filter className="w-4 h-4 mr-2" />
                Clear Filters
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Transactions Table */}
      <DataTable
        data={filteredTransactions}
        columns={columns}
        loading={transactionsLoading}
        testId="transactions-table"
      />

      {/* Transaction Modal */}
      <TransactionModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleSubmitTransaction}
        transaction={selectedTransaction}
      />
    </div>
  );
}

client/src/pages/users.tsx.content:
import { useState } from "react";
import { UserPlus, Edit, Ban } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import DataTable from "@/components/tables/data-table";
import UserModal from "@/components/modals/user-modal";
import { type UserPublic, type InsertUser } from "@shared/schema";
import { useQuery, useMutation } from "@tanstack/react-query";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

export default function Users() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedUser, setSelectedUser] = useState<UserPublic | null>(null);
  const { toast } = useToast();

  // Fetch users from API
  const { data: users = [], isLoading } = useQuery<UserPublic[]>({
    queryKey: ["/api/users"],
  });

  // Create user mutation
  const createUserMutation = useMutation({
    mutationFn: (data: InsertUser) => apiRequest("POST", "/api/users", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User created successfully",
      });
      setIsModalOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create user",
        variant: "destructive",
      });
    },
  });

  // Update user mutation
  const updateUserMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<InsertUser> }) => 
      apiRequest("PUT", `/api/users/${id}`, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User updated successfully",
      });
      setIsModalOpen(false);
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update user",
        variant: "destructive",
      });
    },
  });

  // Delete user mutation
  const deleteUserMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/users/${id}`),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      toast({
        title: "Success",
        description: "User deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete user",
        variant: "destructive",
      });
    },
  });

  const handleAddUser = () => {
    setSelectedUser(null);
    setIsModalOpen(true);
  };

  const handleEditUser = (user: UserPublic) => {
    setSelectedUser(user);
    setIsModalOpen(true);
  };

  const handleSubmitUser = (data: InsertUser) => {
    if (selectedUser) {
      updateUserMutation.mutate({ id: selectedUser.id, data });
    } else {
      createUserMutation.mutate(data);
    }
  };

  const handleToggleUserStatus = (userId: string) => {
    const user = users.find(u => u.id === userId);
    if (user) {
      updateUserMutation.mutate({
        id: userId,
        data: { isActive: !user.isActive }
      });
    }
  };

  const formatLastLogin = (date: Date | null) => {
    if (!date) return "Never";
    return date.toLocaleDateString("en-IN") + " " + date.toLocaleTimeString("en-IN", {
      hour: "2-digit",
      minute: "2-digit",
    });
  };

  const columns = [
    {
      key: "username",
      title: "Username",
      render: (value: string) => (
        <div className="font-medium" data-testid={`user-username-${value}`}>
          {value}
        </div>
      ),
    },
    {
      key: "role",
      title: "Role",
      render: (value: string) => (
        <Badge 
          variant={value === "bookie" ? "default" : "secondary"}
          className={
            value === "bookie" 
              ? "bg-primary/10 text-primary" 
              : "bg-secondary/50 text-secondary-foreground"
          }
          data-testid={`user-role-${value}`}
        >
          {value === "bookie" ? "Bookie" : "Assistant"}
        </Badge>
      ),
    },
    {
      key: "email",
      title: "Email",
      render: (value: string) => (
        <div className="text-sm" data-testid="user-email">
          {value}
        </div>
      ),
    },
    {
      key: "lastLogin",
      title: "Last Login",
      render: (value: Date | null) => (
        <div className="text-sm" data-testid="user-last-login">
          {formatLastLogin(value)}
        </div>
      ),
    },
    {
      key: "isActive",
      title: "Status",
      align: "center" as const,
      render: (value: boolean) => (
        <Badge 
          variant="outline"
          className={value ? "bg-green-100 text-green-600" : "bg-red-100 text-red-600"}
          data-testid={`user-status-${value ? "active" : "inactive"}`}
        >
          {value ? "Active" : "Inactive"}
        </Badge>
      ),
    },
    {
      key: "actions",
      title: "Actions",
      align: "center" as const,
      render: (_: any, row: UserPublic) => (
        <div className="flex justify-center space-x-2">
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleEditUser(row)}
            data-testid={`edit-user-${row.id}`}
          >
            <Edit className="w-4 h-4 text-primary" />
          </Button>
          {row.username !== "admin" && (
            <Button
              variant="ghost"
              size="icon"
              onClick={() => handleToggleUserStatus(row.id)}
              data-testid={`toggle-user-${row.id}`}
            >
              <Ban className="w-4 h-4 text-destructive" />
            </Button>
          )}
        </div>
      ),
    },
  ];

  return (
    <div className="p-4 md:p-6" data-testid="users-page">
      <div className="flex flex-col md:flex-row md:items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-2" data-testid="users-title">
            User Management
          </h2>
          <p className="text-muted-foreground" data-testid="users-description">
            Manage system users and permissions
          </p>
        </div>
        <Button
          onClick={handleAddUser}
          className="mt-4 md:mt-0"
          data-testid="add-user-button"
        >
          <UserPlus className="w-4 h-4 mr-2" />
          Add User
        </Button>
      </div>

      {/* Users Table */}
      <DataTable
        data={users}
        columns={columns}
        testId="users-table"
      />

      {/* User Modal */}
      <UserModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handleSubmitUser}
        user={selectedUser}
      />
    </div>
  );
}

mobile/src/screens/AddTransactionScreen..content:
import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  StyleSheet,
  ScrollView,
  TouchableOpacity,
  SafeAreaView,
  Alert,
} from 'react-native';
import type { AddTransactionScreenProps } from '../types/navigation';

export default function AddTransactionScreen({ navigation }: AddTransactionScreenProps) {
  const [selectedProfile, setSelectedProfile] = useState('');
  const [transactionType, setTransactionType] = useState<'credit' | 'debit'>('credit');
  const [points, setPoints] = useState('');
  const [rate, setRate] = useState('');
  const [commission, setCommission] = useState('');

  const profiles = [
    'Mumbai Kings',
    'Chennai Express', 
    'Delhi Daredevils',
    'Kolkata Knights',
    'Rajasthan Royals'
  ];

  const calculateAmount = () => {
    const pointsNum = parseInt(points) || 0;
    const rateNum = parseInt(rate) || 0;
    return pointsNum * rateNum;
  };

  const calculateCommissionAmount = () => {
    const commissionNum = parseFloat(commission) || 0;
    return (calculateAmount() * commissionNum) / 100;
  };

  const handleSubmit = () => {
    if (!selectedProfile || !points || !rate) {
      Alert.alert('Error', 'Please fill in all required fields');
      return;
    }

    // Here you would typically make an API call to save the transaction
    Alert.alert(
      'Success',
      'Transaction added successfully!',
      [{ text: 'OK', onPress: () => navigation.goBack() }]
    );
  };

  return (
    <SafeAreaView style={styles.container}>
      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        {/* Transaction Type */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Transaction Type</Text>
          <View style={styles.typeSelector}>
            <TouchableOpacity
              style={[
                styles.typeButton,
                transactionType === 'credit' && styles.typeButtonActive
              ]}
              onPress={() => setTransactionType('credit')}
              testID="credit-type-button"
            >
              <Text style={[
                styles.typeButtonText,
                transactionType === 'credit' && styles.typeButtonTextActive
              ]}>
                Credit
              </Text>
            </TouchableOpacity>

            <TouchableOpacity
              style={[
                styles.typeButton,
                transactionType === 'debit' && styles.typeButtonActive
              ]}
              onPress={() => setTransactionType('debit')}
              testID="debit-type-button"
            >
              <Text style={[
                styles.typeButtonText,
                transactionType === 'debit' && styles.typeButtonTextActive
              ]}>
                Debit
              </Text>
            </TouchableOpacity>
          </View>
        </View>

        {/* Profile Selection */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Select Profile *</Text>
          <ScrollView horizontal showsHorizontalScrollIndicator={false} style={styles.profileScroll}>
            <View style={styles.profileList}>
              {profiles.map((profile) => (
                <TouchableOpacity
                  key={profile}
                  style={[
                    styles.profileChip,
                    selectedProfile === profile && styles.profileChipActive
                  ]}
                  onPress={() => setSelectedProfile(profile)}
                  testID={`profile-${profile.replace(' ', '-').toLowerCase()}`}
                >
                  <Text style={[
                    styles.profileChipText,
                    selectedProfile === profile && styles.profileChipTextActive
                  ]}>
                    {profile}
                  </Text>
                </TouchableOpacity>
              ))}
            </View>
          </ScrollView>
        </View>

        {/* Transaction Details */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Transaction Details</Text>

          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>Points *</Text>
            <TextInput
              style={styles.input}
              value={points}
              onChangeText={setPoints}
              placeholder="Enter points"
              keyboardType="numeric"
              testID="points-input"
            />
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>Rate per Point (â‚¹) *</Text>
            <TextInput
              style={styles.input}
              value={rate}
              onChangeText={setRate}
              placeholder="Enter rate"
              keyboardType="numeric"
              testID="rate-input"
            />
          </View>

          <View style={styles.inputContainer}>
            <Text style={styles.inputLabel}>Commission (%)</Text>
            <TextInput
              style={styles.input}
              value={commission}
              onChangeText={setCommission}
              placeholder="Enter commission percentage"
              keyboardType="numeric"
              testID="commission-input"
            />
          </View>
        </View>

        {/* Calculation Summary */}
        {points && rate && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Calculation Summary</Text>
            <View style={styles.summaryCard}>
              <View style={styles.summaryRow}>
                <Text style={styles.summaryLabel}>Points:</Text>
