import { insertProfileSchema, type InsertProfile, type Profile } from "@shared/schema";

interface ProfileModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: InsertProfile) => void;
  profile?: Profile | null;
}

export default function ProfileModal({
  isOpen,
