import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { insertUserSchema, type InsertUser, type User } from "@shared/schema";
import { z } from "zod";

interface UserModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSubmit: (data: InsertUser) => void;
  user?: User | null;
}

export default function UserModal({
  isOpen,
  onClose,
  onSubmit,
  user,
}: UserModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const isEditing = !!user;

  // Create dynamic schema based on editing mode
  const formSchema = isEditing 
    ? insertUserSchema.extend({
        password: z.string().optional().or(z.literal("")),
      })
    : insertUserSchema;

  const form = useForm<InsertUser>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      username: "",
      email: "",
      password: "",
      role: "assistant",
    },
  });

  useEffect(() => {
    if (user) {
      form.reset({
        username: user.username,
        email: user.email,
        password: "", // Don't pre-fill password for security
        role: user.role as "bookie" | "assistant",
      });
    } else {
      form.reset({
        username: "",
        email: "",
        password: "",
        role: "assistant",
      });
    }
  }, [user, form]);

  const handleSubmit = async (data: InsertUser) => {
    setIsSubmitting(true);
    try {
      // If editing and password is empty, remove it from the data
      if (isEditing && (!data.password || data.password === "")) {
        const { password, ...dataWithoutPassword } = data;
        onSubmit(dataWithoutPassword as InsertUser);
      } else {
        onSubmit(data);
      }
      onClose();
    } catch (error) {
      console.error("Error submitting user:", error);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-md" data-testid="user-modal">
        <DialogHeader>
          <DialogTitle data-testid="user-modal-title">
            {isEditing ? "Edit User" : "Add User"}
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="username"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    Username <span className="text-destructive">*</span>
                  </FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Enter username"
                      {...field}
                      data-testid="user-username-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="email"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    Email <span className="text-destructive">*</span>
                  </FormLabel>
                  <FormControl>
                    <Input
                      type="email"
                      placeholder="user@example.com"
                      {...field}
                      data-testid="user-email-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="password"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    Password <span className="text-destructive">*</span>
                  </FormLabel>
                  <FormControl>
                    <Input
                      type="password"
                      placeholder={isEditing ? "Leave blank to keep current password" : "Min 8 characters"}
                      {...field}
                      data-testid="user-password-input"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="role"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>
                    Role <span className="text-destructive">*</span>
                  </FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="user-role-select">
                        <SelectValue placeholder="Select Role" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="bookie">Bookie (Full Access)</SelectItem>
                      <SelectItem value="assistant">Assistant (Limited Access)</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex space-x-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={onClose}
                className="flex-1"
                data-testid="user-cancel-button"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={isSubmitting}
                className="flex-1"
                data-testid="user-submit-button"
              >
                {isSubmitting ? "Saving..." : isEditing ? "Update User" : "Create User"}
